import axios from 'axios';
import React, { SyntheticEvent, useState } from 'react'
import { Redirect } from 'react-router-dom';

const Login=()=> {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [redirect, setRedirect] = useState(false)
  const [errors, setError]=useState("")

  const handleLogin = async (e: SyntheticEvent) => {

    e.preventDefault();
    axios({
      url: "/login",
      method: "POST",
      data: JSON.stringify({
        email,
        password,
      }),
    })
      .then(async (res) => {
        console.log(res);

        const content = await res;
        const readData=content.data
        if (readData.status===200){
            setRedirect(true)
        }
        if(readData.status===401 && readData.message==="Unauthorized"){
          const response=`Please provide a valid email and password!`
          setError(response)
        }
      })
      .catch((err) => {
        setError(err.data.message)
        console.log(err.data);
      });

  };

  if(redirect){
    return  <Redirect to ="/"/>;
  }

    return (
      <div>
      <div className="err">
        {errors}
      </div>
        <form onSubmit={handleLogin}>
        <h1 className="h3 mb-3 fw-normal">Login</h1>

        <input
          type="email"
          className="form-control"
          placeholder="Email address"
          required
          onChange={(e) => setEmail(e.target.value)}
        />

        <input
          type="password"
          className="form-control"
          required
          placeholder="Password"
          onChange={(e) => setPassword(e.target.value)}
        />

        <button
          className="w-100 btn btn-lg btn-primary custom-btn"
          type="submit"
        >
          Login
        </button>
      </form>
      </div>
    )
}

export default  Login;
