import axios from "axios";
import React, { SyntheticEvent, useState } from "react";
import { Redirect } from "react-router-dom";

const Register = () => {
  const [displayName, setDisplayName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [redirect, setRedirect] = useState(false)
  const [errors, setError]=useState("")

  const handleSubmit = async (e: SyntheticEvent) => {
    e.preventDefault();
    axios({
      url: "/register",
      method: "POST",
      data: JSON.stringify({
        displayName,
        email,
        password,
      }),
    })
      .then(async (res) => {
        const content = await res;
        const readData=content.data
        if (readData.status===200){
            setRedirect(true)
        }
        if(readData.status===409 && readData.message==="Conflict"){
          const response=`${email} already exist, please login to access your account`
          setError(response)
        }

      })
      .catch((err) => {
        setError(err.data.message)
        console.log(err.data);

      });

  };

  if(redirect){
    return  <Redirect to ="/login"/>;
  }


  return (
    <div>
      <div className="err">
        {errors}
      </div>
    <form onSubmit={handleSubmit}>
      <h1 className="h3 mb-3 fw-normal">Register</h1>
      <input
        className="form-control"
        required
        placeholder="Name"
        onChange={(e) => setDisplayName(e.target.value)}
      />

      <input
        type="email"
        className="form-control text-lowercase"
        placeholder="Email address"
        autoCapitalize = 'none'
        required
        onChange={(e) => setEmail(e.target.value)}
      />

      <input
        type="password"
        className="form-control"
        required
        placeholder="Password"
        onChange={(e) => setPassword(e.target.value)}
      />

      <button className="w-100 btn btn-lg btn-primary custom-btn" type="submit">
        Submit
      </button>
    </form>
    </div>
  );
};
export default Register;
