import React, { useContext } from 'react';
import { useHistory } from 'react-router-dom';
import { Button } from 'reactstrap';

import IPageProps from '../interfaces/pages';
import CenterPiece from '../components/centerpiece';
import UserContext from '../context/user';

const LogoutPage: React.FunctionComponent<IPageProps> = (props) => {
    const userContext = useContext(UserContext);
    const { user } = userContext.userState;
    const history = useHistory();

    const Logout = () => {
        userContext.userDispatch({ type: 'logout', payload: userContext.userState });
        history.push('/login');
    };

    return (
        <CenterPiece header="Logout">
            <p className="text-center">Are you sure you want to logout?</p>
            <div className="text-center">
                <Button color="danger" className="mr-2" onClick={() => history.goBack()}>
                    Cancel
                </Button>
                <Button color="info" className="mr-2" onClick={() => Logout()}>
                    Logout
                </Button>
            </div>
        </CenterPiece>
    );
};

export default LogoutPage;
