import axios from 'axios';
import React, { useState } from 'react'
import { Link } from 'react-router-dom';
import { Container } from 'reactstrap';
import Header from '../components/header';
import LoadingComponent from '../components/loading_components';
import config from '../config/config';
import IBlog from '../interfaces/blog';
// import Naviagtion from '../components/navigation';
import IPageProps from "../interfaces/pages";


const HomePage:React.FunctionComponent<IPageProps>=props=>{
    const [blogs, setBlogs] = useState<IBlog[]>([]);
    const [loading, setLoading] = useState<boolean>(true)
    const [error, setError] = useState<string>('');

    const getAllBlogs = async () => {
        try 
        {
            const response = await axios({
                method: 'GET',
                url: `${config.server.url}/blogs`,
            });

            if (response.status === (200 || 304))
            {
                let blogs = response.data.blogs as IBlog[];
                blogs.sort((x,y) => y.updatedAt.localeCompare(x.updatedAt));

                setBlogs(blogs);
            }
            else
            {
                setError('Unable to retrieve blogs');
            }
        } 
        catch (error) 
        {
            setError(error.message);
        }
        finally
        {
            setTimeout(() => {
                setLoading(false)
            }, 500)
        }
    }

    if (loading) 
    {
        return <LoadingComponent>Loading blogs...</LoadingComponent>
    }
    return(
       <Container fluid className="p-0">
           {/* <Naviagtion/> */}
           <Header title="Blog" headline="checkout the my blog"/>

        <Container style={{color:'white'}} className="mt-5" >
        {blogs.length === 0 && <p>There are no blogs yet.  You should <Link to="/edit">post</Link> one ðŸ˜Š.</p>}
            Blog Stuff .....
        </Container>

       </Container>
    );
}


export default HomePage;
