import React, { useContext, useState } from 'react';
import { Link } from 'react-router-dom';
import { Button, ButtonDropdown, Container, DropdownItem, DropdownMenu, DropdownToggle, Modal, ModalBody, ModalFooter, ModalHeader, Nav, Navbar, NavbarBrand, NavbarText } from 'reactstrap';
import { auth } from '../../config/firebase';
import UserContext from '../../context/user';
import CenterPiece from '../centerpiece';

export interface INavigationProps {}

const Naviagtion: React.FunctionComponent<INavigationProps> = (props) => {
    const userContext = useContext(UserContext);
    const { user } = userContext.userState;
    const [dropdownOpen, setOpen] = useState(false);
    const [modal, setModal] = useState(false);

    const toggle = () => setOpen(!dropdownOpen);

    const toggleModal = () => setModal(!modal);

    const Logout = () => {
        userContext.userDispatch({ type: 'logout', payload: userContext.userState });

    };

    return (
        <Navbar color="light;" sticky="top" expand="md" style={{ backgroundColor: '#222454', color: 'white' }}>
            <Container>
                <NavbarBrand tag={Link} to="/">
                    AdeBlog
                </NavbarBrand>
                <Nav className="mr-auto" navbar />
                <Nav className="mr-auto" navbar></Nav>
                {user._id !== '' ? (
                    <div>
                        <Button outline size="sm" tag={Link} to="/edit">
                            <i className="far fa-sticky-note mr-2"></i>
                            Post a Blog
                        </Button>


                        <Button color="danger" onClick={toggleModal}>modal</Button>
      <Modal isOpen={modal} toggle={toggleModal} >
      <ModalHeader header="Logout" className=" text-white" style={{backgroundColor:'#222454'}}>Logout</ModalHeader>
      <ModalBody>
      <p className="text-center">Are you sure you want to logout?</p>
          </ModalBody> 
          <ModalFooter><div className="text-center">
                <Button color="danger" className="mr-2" onClick={toggleModal}>
                    Cancel
                </Button>
                <Button color="info" className="mr-2" onClick={() => Logout()}>
                    Logout
                </Button>
            </div>
            </ModalFooter> 
        {/* </ModalHeader> */}
        {/* <ModalHeader toggle={toggle}>Modal title</ModalHeader>
        <ModalBody>
          Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
        </ModalBody>
        <ModalFooter>
          <Button color="primary" onClick={toggleModal}>Do Something</Button>{' '}
          <Button color="secondary" onClick={toggleModal}>Cancel</Button>
        </ModalFooter> */}
      </Modal>
                        {/* <NavbarText className="ml-2 mr-2">|</NavbarText> */}
                        {/* <Button outline size="sm" onClick={() => logout()}>
                            Logout
                        </Button> */}

                        <ButtonDropdown className="ml-3 mr-3" isOpen={dropdownOpen} toggle={toggle}>
                            <DropdownToggle className="ml-2 mr-2">
                                <i className="fas fa-user-circle fa-lg"></i>
                            </DropdownToggle>
                            <DropdownMenu>
                                <DropdownItem tag={Link} to="/">
                                    Account
                                </DropdownItem>
                                {auth.currentUser?.providerData[0]?.providerId === 'password' ? (
                                    <DropdownItem tag={Link} to="/change">
                                        Change Password
                                    </DropdownItem>
                                ) : (
                                    ''
                                )}
                                <DropdownItem divider />
                                <DropdownItem style={{ color: 'red' }} tag={Link} to="/logout">
                                    Logout
                                </DropdownItem>
                            </DropdownMenu>
                        </ButtonDropdown>
                        <NavbarText className="ml-2 mr-2"></NavbarText>
                    </div>
                ) : (
                    <div>
                        <NavbarText tag={Link} to="/login">
                            Login
                        </NavbarText>
                        <NavbarText className="ml-2 mr-2">|</NavbarText>
                        <NavbarText tag={Link} to="/register">
                            Signup
                        </NavbarText>
                    </div>
                )}
            </Container>
        </Navbar>
    );
};

export default Naviagtion;
